import React from 'react'

import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";
import '../../../../src/Style/style.css';
import { Autoplay, Pagination, Navigation, Scrollbar } from "swiper";
import CardSlider from './CardSlider';

import Img from '../../../images/Ellipse 4.png'
import Img2 from '../../../images/Ellipse 4 (1).png'
import Img3 from '../../../images/Ellipse 4 (2).png'




function SectionSlider() {
    return (
        <section id="section-slider-crcle">
            <div class="crcle-title">
                <h3 class="title">An enterprise template to ramp up your company website</h3>
                <Swiper
                    slidesPerView={3}
                    spaceBetween={10}
                    slidesPerGroup={1}
                    speed={1000}
                    loop={true}
                    scrollbar={{ draggable: true }}
                    loopFillGroupWithBlank={true}
                    autoplay={{
                        delay: 2500,
                        disableOnInteraction: false,
                    }}
                    breakpoints={{
                        "@0.00": {
                            slidesPerView: 1,
                            spaceBetween: 10,
                        },
                        "@0.5": {
                            slidesPerView: 1,
                            spaceBetween: 10,
                        },
                        "@0.75": {
                            slidesPerView: 2,
                            spaceBetween: 10,
                        },
                        "@1.00": {
                            slidesPerView: 3,
                            spaceBetween: 10,
                        }
                    }}
                    navigation={true}
                    modules={[Autoplay, Pagination, Navigation, Scrollbar]}
                    className="mySwiper"
                >
                    <div className="section-slider-crcle-container">
                        <SwiperSlide>
                            <CardSlider
                                P={"Buyer buzz partner network disruptive non-disclosure agreement business"}
                                H4={"Albus Dumbledore"}
                                AP={'Manager @ Howarts'}
                                IMG={Img}
                            />

                        </SwiperSlide>
                        <SwiperSlide>
                            <CardSlider
                                P={"Buyer buzz partner network disruptive non-disclosure agreement business"}
                                H4={"Severus Snape"}
                                AP={'Manager @ Howarts'}
                                IMG={Img2}
                            />
                        </SwiperSlide>
                        <SwiperSlide>
                            <CardSlider
                                P={"Buyer buzz partner network disruptive non-disclosure agreement business"}
                                H4={"Harry Potter"}
                                AP={'Team Leader @ Gryffindor'}
                                IMG={Img3}
                            />
                        </SwiperSlide>
                        <SwiperSlide>
                            <CardSlider
                                P={"Buyer buzz partner network disruptive non-disclosure agreement business"}
                                H4={"Albus Dumbledore"}
                                AP={'Manager @ Howarts'}
                                IMG={Img}
                            />

                        </SwiperSlide>
                        <SwiperSlide>
                            <CardSlider
                                P={"Buyer buzz partner network disruptive non-disclosure agreement business"}
                                H4={"Severus Snape"}
                                AP={'Manager @ Howarts'}
                                IMG={Img2}
                            />
                        </SwiperSlide>
                        <SwiperSlide>
                            <CardSlider
                                P={"Buyer buzz partner network disruptive non-disclosure agreement business"}
                                H4={"Harry Potter"}
                                AP={'Team Leader @ Gryffindor'}
                                IMG={Img3}
                            />
                        </SwiperSlide>
                    </div>


                </Swiper>
            </div>

        </section >
    )
}

export default SectionSlider
