import React from 'react'

import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css";
import "swiper/css/pagination";
import "swiper/css/navigation";
import '../../../../src/Style/style.css'

import { Autoplay, Pagination, Navigation, Scrollbar } from "swiper";

function SectionSlider() {
    return (
        // <div>

        //         
        // </div>
        <section id="section-slider-crcle">
            <div class="crcle-title">
                <h3 class="title">An enterprise template to ramp up your company website</h3>
                <div class="crcle-slider-svg">
                    <button>
                        <i class="fa-solid fa-arrow-left"></i>
                    </button>
                    <button>
                        <i class="fa-solid fa-arrow-right"></i>
                    </button>

                </div>
                <Swiper
                    slidesPerView={3}
                    spaceBetween={10}
                    slidesPerGroup={1}
                    speed={1000}
                    loop={true}
                    scrollbar={{ draggable: true }}
                    loopFillGroupWithBlank={true}
                    autoplay={{
                        delay: 2500,
                        disableOnInteraction: false,
                    }}
                    breakpoints={{
                        "@0.00": {
                            slidesPerView: 1,
                            spaceBetween: 10,
                        },
                        "@0.5": {
                            slidesPerView: 1,
                            spaceBetween: 10,
                        },
                        "@0.75": {
                            slidesPerView: 2,
                            spaceBetween: 10,
                        },
                        "@1.00": {
                            slidesPerView: 3,
                            spaceBetween: 10,
                        }
                    }}
                    // navigation={true}
                    modules={[Autoplay, Pagination, Navigation, Scrollbar]}
                    className="mySwiper"
                >
                    .section-slider-crcle-container


                </Swiper>


                <div class="section-slider-crcle-container">
                    <div class="section-slider-card-crcle">
                        <p>“Buyer buzz partner network disruptive non-disclosure agreement business”</p>
                        <div class="section-alt-slider-card">
                            <div class="crcle-alt-img">
                                <img src="images/Ellipse 4.png" alt="" />
                            </div>
                            <div class="crcle-alt-text">
                                <h4>Albus Dumbledore</h4>
                                <p>Manager @ Howarts</p>
                            </div>
                        </div>
                    </div>
                    <div class="section-slider-card-crcle">
                        <p>“Buyer buzz partner network disruptive non-disclosure agreement business”</p>
                        <div class="section-alt-slider-card">
                            <div class="crcle-alt-img">
                                <img src="images/Ellipse 4 (1).png" alt="" />
                            </div>
                            <div class="crcle-alt-text">
                                <h4>Severus Snape</h4>
                                <p>Manager @ Slytherin</p>
                            </div>
                        </div>
                    </div>
                    <div class="section-slider-card-crcle">
                        <p>“Buyer buzz partner network disruptive non-disclosure agreement business”</p>
                        <div class="section-alt-slider-card">
                            <div class="crcle-alt-img">
                                <img src="images/Ellipse 4 (2).png" alt="" />
                            </div>
                            <div class="crcle-alt-text">
                                <h4>Harry Potter</h4>
                                <p>Team Leader @ Gryffindor</p>
                            </div>
                        </div>
                    </div>

                </div>
            </div>

        </section >
    )
}

export default SectionSlider
