import React, { createContext, useContext } from 'react';

var Context = createContext({});

// @ts-ignore
function useConnect(mapStateToProps) {
    var store = useContext(Context);
    var mappedState = mapStateToProps(store.getState());
    return [store.dispatch, mappedState];
}

function Provider(props) {
    return (React.createElement(Context.Provider, { value: props.store }, props.children));
}

export default useConnect;
export { Provider };
